(21, 'Organogram of BIGM', NULL, '<figure class=\"easyimage easyimage-full\"><img alt=\"\" src=\"blob:http://nanoit.biz/c36a5ef0-d5ce-41b4-985d-3cc68d6fdbd6\" width=\"1693\" />\r\n<figcaption></figcaption>\r\n</figure>\r\n\r\n<p>&nbsp;</p>', 1, 1, 4, NULL, '2021-07-08 18:12:46', '2021-10-06 15:25:26'),
(24, 'Vision and Mission', NULL, '<p style=\"text-align: center; \"><img src=\"data:image/jpeg;base64,\" style=\"width: 350px;\" data-filename=\"vision-and-mission.jpg\"><b><span style=\"font-family: Verdana; color: rgb(0, 0, 255);\"><br></span></b></p><p><b><span style=\"font-family: Verdana; color: rgb(0, 0, 255);\">Vision</span></b></p><p style=\"text-align: justify; \"><font color=\"#333333\" face=\"Verdana\"><span style=\"font-size: 14px;\">Vision of the Institute is to become a center&nbsp;of excellence in teaching and research on policy options regarding governance and development and to establish an effective, transparent and accountable public service in Bangladesh.</span></font></p><p style=\"text-align: justify; \"><font color=\"#333333\" face=\"Verdana\"><span style=\"font-size: 14px;\"><br></span></font><br></p><p><span style=\"font-size: 1rem; font-family: Verdana; color: rgb(255, 0, 0);\"><b>Mission</b></span></p><p style=\"text-align: justify; \"><span style=\"color: rgb(51, 51, 51); font-family: Verdana; font-size: 14px;\">Mission of the Institute is the capacity development of the public and private sector executives through strengthening their knowledge, skills, and competence and by enhancing their commitment, motivation, and dedication to the interest of the country and its people.</span><span style=\"font-size: 1rem;\"><br></span></p>', 1, 2, 2, NULL, '2021-08-09 21:41:03', '2021-08-25 15:48:33');
